import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { useParams } from 'react-router-dom';

function PokemonPage({ list }) {
  let { pokemonName } = useParams();
  const [pokemon, setPokemon] = useState(null);

  useEffect(() => {
    // Trova il Pokémon corrispondente nell'elenco e aggiorna lo stato locale
    const foundPokemon = list.find(p => p.name === pokemonName);
    setPokemon(foundPokemon);
  }, [pokemonName, list]); // Aggiungi list come dipendenza se può cambiare

  if (!pokemon) {
    return <div>Pokémon non trovato</div>;
  }

  return (
    <div className="pokemon_card">
        <img className="pokemon_img" src={pokemon.sprites.front_default} alt={pokemon.name} />
        <div className='pokemon_name'>{pokemon.name}</div>
    </div>
  );
}

const mapStateToProps = state => {
  return {
    list: state.pokemon.list,
  };
};

export default connect(mapStateToProps)(PokemonPage);
