import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { useParams } from 'react-router-dom';

function CatFact({selectedCatFact, facts, fetchCatFactsList, loading, error }) {
  const { index } = useParams();

  useEffect(() => {
    if (!facts || !facts.list || facts.list.length === 0 || !loading) {
        fetchCatFactsList();
    }
    const catFactIndex = parseInt(index, 10);
    const selectedCatFact = facts[catFactIndex]
  }, [index, ]);

  if (loading) {
    return <div><CatLoad></CatLoad></div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      {selectedCatFact ? (
        <div>
          <h2>Cat Fact #{index}</h2>
          <img src={selectedCatFact.img} alt="Cat Image" />
          <div className="preview">{selectedCatFact.fact}</div>
        </div>
      ) : (
        <div><CatLoad></CatLoad></div>
      )}
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    facts: state.cat_fact.facts,
    loading: state.cat_fact.loading,
    error: state.cat_fact.error,
  };
};

export default connect(mapStateToProps, { fetchCatFactsList })(CatFact);
