import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { fetchCatFactsList } from '../store/actions/cat_fact';
import '../styles/homepage.css';
import CatLoad from './cat_load';
import CatCard from './cat_card';
import ScrollToTopButton from './scroll_btn';

function CatFactsList({ facts, loading, error, fetchCatFactsList }) {
    useEffect(() => {
        if (!facts || !facts.list || facts.list.length === 0) {
            fetchCatFactsList();
        }
    }, []);

    if (loading) {
        return <div><CatLoad></CatLoad></div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="cat_facts_container">
            {facts && facts.list && facts.list.map((cat_fact, index) => {
                return (
                    <CatCard cat_fact={cat_fact} key={index} index={index}></CatCard>
                )
            }
            )}
        </div>
    );
}

const mapStateToProps = state => {
    return {
        facts: state.cat_fact.facts,
        loading: state.cat_fact.loading,
        error: state.cat_fact.error,
    };
};

export default connect(mapStateToProps, { fetchCatFactsList })(CatFactsList);
