import React, { useState, useEffect } from 'react';
import { FaArrowUp } from 'react-icons/fa';
import './ScrollToTopButton.css'; // Aggiungi il tuo stile CSS se necessario

const ScrollToTopButton = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Aggiungi un listener per verificare la posizione dello scroll
    const handleScroll = () => {
      const scrollY = window.scrollY;

      // Imposta isVisible in base alla posizione dello scroll
      setIsVisible(scrollY > 100);
    };

    window.addEventListener('scroll', handleScroll);

    // Rimuovi il listener quando il componente viene smontato
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth', // Aggiunge un effetto di scorrimento fluido
    });
  };

  return (
    <div>
      {isVisible && (
        <button className="scroll-to-top-button" onClick={scrollToTop}>
          <FaArrowUp />
        </button>
      )}
    </div>
  );
};

export default ScrollToTopButton;
