import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { useParams } from 'react-router-dom';

function CatFact({selectedCatFact, facts, fetchCatFact }) {
  const { index } = useParams();

  useEffect(() => {
    const catFactIndex = parseInt(index, 10);
    const selectedCatFact = facts[catFactIndex]
  }, [index, ]);

  return (
    <div>
      {selectedCatFact ? (
        <div>
          <h2>Cat Fact #{index}</h2>
          <img src={selectedCatFact.img} alt="Cat Image" />
          <div className="preview">{selectedCatFact.fact}</div>
        </div>
      ) : (
        <p>Caricamento...</p>
      )}
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    facts: state.cat_fact.facts,
  };
};

export default connect(mapStateToProps, { fetchCatFact })(CatFact);
