// reducers.js
import {
  FETCH_CAT_FACT_LIST_REQUEST, FETCH_CAT_FACT_LIST_SUCCESS, FETCH_CAT_FACT_LIST_FAILURE,
} from '../actions/pokemon';

const initialState = {
  list: [],
  loading: false,
  error: null,
  evolution_chain: [],
};

//built the request, success, failure structure but still not used for the evolution

const pokemonReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_CAT_FACT_LIST_REQUEST:
      return { ...state, loading: true, error: null };
    case FETCH_CAT_FACT_LIST_SUCCESS:
      return { ...state, list: action.payload, loading: false };
    case FETCH_CAT_FACT_LIST_FAILURE:
      return { ...state, error: action.payload, loading: false };
    default:
      return state;
  }
};

export default pokemonReducer;
