// actions.js
export const FETCH_POKEMON_LIST_REQUEST = 'FETCH_POKEMON_LIST_REQUEST';
export const FETCH_POKEMON_LIST_SUCCESS = 'FETCH_POKEMON_LIST_SUCCESS';
export const FETCH_POKEMON_LIST_FAILURE = 'FETCH_POKEMON_LIST_FAILURE';

export const FETCH_POKEMON_REQUEST = 'FETCH_POKEMON_REQUEST';
export const FETCH_POKEMON_SUCCESS = 'FETCH_POKEMON_SUCCESS';
export const FETCH_POKEMON_FAILURE = 'FETCH_POKEMON_FAILURE';


const updatePokemonList = (list) => {
    return {
        type: FETCH_POKEMON_LIST_SUCCESS,
        payload: list
    }
}

export const fetchPokemonList = () => {
    return async dispatch => {
        dispatch({ type: FETCH_POKEMON_LIST_REQUEST });
        try {
            const res = await fetch('https://pokeapi.co/api/v2/pokemon/');
            console.log(res);
            if (!res.ok) {
                throw new Error('Errore nella richiesta');
            }    
            const list = await res.json();
            dispatch(updatePokemonList(list.results));
        }     
        catch (error) {
            dispatch({ type: FETCH_POKEMON_LIST_FAILURE, payload: error.message });
        }    
    }    
};    


const setPokemonInfo = (data) => {
    return {
        type: FETCH_POKEMON_SUCCESS,
        payload: data
    }
}

export const fetchPokemon = (url) => {
    return async dispatch => {
        dispatch({ type: FETCH_POKEMON_REQUEST });
        try {
            const res = await fetch('https://pokeapi.co/api/v2/pokemon/'+url);
            console.log(res);
            if (!res.ok) {
                throw new Error('Errore nella richiesta');
            }
            const data = await res.json();
            dispatch(setPokemonInfo(data));
        } 
        catch (error) {
            dispatch({ type: FETCH_POKEMON_FAILURE, payload: error.message });
        }
    }
};
