// reducers.js
import {
  FETCH_CAT_FACT_LIST_REQUEST, FETCH_CAT_FACT_LIST_SUCCESS, FETCH_CAT_FACT_LIST_FAILURE,
} from '../actions/cat_fact';

const initialState = {
  cats_facts: {
    list: [],
    current_page: 0,
    first_page_url: '',
    last_page_url: '',
  },
  loading: false,
  error: null,
};

const cat_fact_reducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_CAT_FACT_LIST_REQUEST:
      return { ...state, loading: true, error: null };
    case FETCH_CAT_FACT_LIST_SUCCESS:
      return { 
        ...state, 
        cats_facts: {
          list: [...state.cats_facts.list || [], action.payload.data], 
          current_page: action.payload.current_page,
          first_page_url: action.payload.first_page_url,
          last_page_url: action.payload.last_page_url,
        },
        loading: false };
    case FETCH_CAT_FACT_LIST_FAILURE:
      return { ...state, error: action.payload, loading: false };
    default:
      return state;
  }
};

export default cat_fact_reducer;
