import { combineReducers } from 'redux';
import counterReducer from './counterReducer'; // Importa il tuo reducer per il contatore
import userReducer from './userReducer'; // Importa il tuo reducer per l'utente (o qualsiasi altro reducer)

// Combina i tuoi reducer in un'unica radice
const rootReducer = combineReducers({
  counter: counterReducer,
  user: userReducer,
  // Aggiungi altri reducer qui se necessario
});

export default rootReducer;
