import React, { useEffect, useRef } from 'react';
import { connect } from 'react-redux';
import { fetchCatFactsList } from '../store/actions/cat_fact';
import '../styles/homepage.css';
import CatLoad from './cat_load';
import CatCard from './cat_card';

function HomePage({ facts, loading, error, fetchCatFactsList }) {
  const buttonsContainerRef = useRef(null);

    // Variabili per gestire l'inizio dello scroll
    let isScrolling = false;
    let startX = 0;

    // Gestisce l'inizio dello scroll
    const handleMouseDown = (e) => {
      isScrolling = true;
      startX = e.clientX || e.touches[0].clientX;
    };

    // Gestisce il movimento durante lo scroll
    const handleMouseMove = (e) => {
      if (!isScrolling) return;

      const currentX = e.clientX || e.touches[0].clientX;
      const scrollDelta = startX - currentX;
      buttonsContainerRef.current.scrollLeft += scrollDelta;

      startX = currentX;
    };

    // Gestisce la fine dello scroll
    const handleMouseUp = () => {
      setTimeout(() => {
        isScrolling = false;
      }, 100);
    };

  const scrollToButton = (buttonIndex) => {
    const buttonOffset = buttonIndex * 50; // Considera che ogni bottone ha una larghezza stimata di 50px

    // Calcola l'offset necessario per centrare il bottone nella finestra
    const centerOffset = buttonOffset - window.innerWidth / 2 + 25; // Considera metà della larghezza del bottone

    // Esegui lo scroll programmato
    buttonsContainerRef.current.scrollLeft = centerOffset;
  };


  useEffect(() => {
    if (!facts || !facts.list || facts.list.length === 0) {
      fetchCatFactsList();
    }
  }, [facts, fetchCatFactsList]);

  if (loading) {
    return <div><CatLoad></CatLoad></div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="homepage">
      <h2>Wanna know some cats facts?</h2>
      <div className='pages'>pages:</div>
      <div className='page_indicator' ref={buttonsContainerRef}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onTouchStart={handleMouseDown}
        onTouchMove={handleMouseMove}
        onTouchEnd={handleMouseUp}>
        {Array.from({ length: facts.last_page }, (_, index) => index + 1).map((value) => (
          <button key={value} onClick={() => scrollToButton(value)}>{value}</button>
        ))}
      </div>

      <div className="cat_fact_container">
        {facts && facts.list && facts.list.map((cat_fact, index) => {
          return (
            <CatCard cat_fact={cat_fact} key={index} index={index}></CatCard>
          )
        }
        )}
      </div>
    </div>
  );
}


const mapStateToProps = state => {
  return {
    facts: state.cat_fact.facts,
    loading: state.cat_fact.loading,
    error: state.cat_fact.error,
  };
};

export default connect(mapStateToProps, { fetchCatFactsList })(HomePage);
