import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { fetchCatFactsList } from '../store/actions/pokemon';
import PokemonCard from '../components/pokemon_card';
import '../styles/homepage.css';

function HomePage({ list, loading, error, fetchCatFactsList }) {
  useEffect(() => {
    if(!list || list.length === 0){
      fetchCatFactsList();
    }
  }, [list, fetchCatFactsList]);

  if (loading) {
    return <div><CatLoad></CatLoad></div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="homepage">
      <h2>Pokemon List</h2>
      <div className="pokemon_container">
        {list && list.map(pokemon => (
          <PokemonCard key={pokemon.name} pokemon={pokemon} animated={false}></PokemonCard>
        ))}
      </div>
    </div>
  );
}

const mapStateToProps = state => {
  return {
    list: state.pokemon.list,
    loading: state.pokemon.loading,
    error: state.pokemon.error,
  };
};

export default connect(mapStateToProps, { fetchCatFactsList })(HomePage);
