import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { useParams } from 'react-router-dom';
import { fetchPokemonList } from '../store/actions/pokemon';

function PokemonPage({ list, loading, error, fetchPokemonList }) {
    let { pokemonName } = useParams();
    const [pokemon, setPokemon] = useState(null);

    useEffect(() => {
        if (list.length === 0) {
            fetchPokemonList();
        }
        else {
            const foundPokemon = list.find(p => p.name === pokemonName);
            setPokemon(foundPokemon);
        }
    }, [pokemonName, list]);

    if (!pokemon) {
        return <div>Pokemon not found :(</div>;
    }

    return (
        <section className="pokemon_page">
            <div style={{ display: 'flex' }}>
                <h2>{pokemon.name}</h2>
                <div className='pokedex_number'>{pokemon.id}</div>
            </div>
            <div className="front_back_images">
                <img className="pokemon_img" src={pokemon.sprites.front_default}></img>
                <img className="pokemon_img" src={pokemon.sprites.back_default}></img>
            </div>
            {pokemon.types.map(t =>
                <div key={t.slot} className="pokemon_card">
                    <div>{t.type.name}</div>
                </div>
            )}
            <div>Statistiche del Pokèmon e somma delle statistiche</div>
            <div>Altezza e peso del Pokèmon</div>
            <div>Elenco delle evoluzioni con nome e immagine dell’evoluzione (sprite) che</div>
            <div>al click mandi alla pagina dettaglio dell’evoluzione</div>
        </section>
    );
}

const mapStateToProps = state => {
    return {
        list: state.pokemon.list,
        loading: state.pokemon.loading,
        error: state.pokemon.error,
    };
};

export default connect(mapStateToProps, { fetchPokemonList })(PokemonPage);
