import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { fetchPokemon } from '../store/actions/pokemon';

function HomePage({ list, loading, error, fetchPokemon }) {
  useEffect(() => {
    fetchPokemon();
  }, [fetchPokemon]);

  if (loading) {
    return <div>Caricamento...</div>;
  }

  if (error) {
    return <div>Errore: {error}</div>;
  }

  return (
    <div>
      <h1>Elenco dei Pok√©mon</h1>
      <ul>
        {list && list.map(pokemon => (
          <li key={pokemon.id}>{pokemon.name}</li>
        ))}
      </ul>
    </div>
  );
}

const stateProps = state => {
  return {
    list: state.pokemon.list,
    loading: state.pokemon.loading,
    error: state.pokemon.error,
  };
};

export default connect(stateProps, { fetchPokemon })(HomePage);
