import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { fetchPokemon } from '../store/actions/pokemon';

function HomePage({ pokelist, loading, error, fetchPokemon }) {
  useEffect(() => {
    fetchPokemon();
  }, [fetchPokemon]);

  if (loading) {
    return <div>Caricamento...</div>;
  }

  if (error) {
    return <div>Errore: {error}</div>;
  }

  if (!pokelist || pokelist.length === 0) {
    return null; // Nessun elemento nella lista o la lista è vuota, quindi non mostriamo nulla
  }

  return (
    <>
      <h1>POKE APP</h1>
      {pokelist.map(pokemon => (
        <div key={pokemon.id}>
          <h2>{pokemon.name}</h2>
          <p>Tipo: {pokemon.type}</p>
        </div>
      ))}
    </>
  )

  const stateProps = state => {
    return {
      list: state.pokemon.list,
      loading: state.pokemon.loading,
      error: state.pokemon.error,
    };
  };
}

  export default connect(stateProps, { fetchPokemon })(HomePage);
