import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { fetchCatFactsList } from '../store/actions/cat_fact';
import '../styles/homepage.css';
import CatLoad from './cat_load';
import CatFactsList from './cat_facts_list';
import ScrollToTopButton from './scroll_btn';

function HomePage({ facts, loading, error, fetchCatFactsList }) {
  const [visibleCount, setVisibleCount] = useState(10); // Numero di elementi inizialmente visibili
  const itemsPerPage = 10; // Numero di elementi da caricare ad ogni "Show More"

  useEffect(() => {
    if (!facts || !facts.list || facts.list.length === 0) {
      fetchCatFactsList();
    }
  }, []);

  const handleShowMore = () => {
    setVisibleCount((prevVisibleCount) => prevVisibleCount + itemsPerPage);
    // Chiamare la tua azione per caricare ulteriori elementi se necessario
    // fetchCatFactsList(facts.current_page + 1);
  };

  if (loading) {
    return <div><CatLoad></CatLoad></div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <div className="homepage">
        <h2>Wanna know some cats facts?</h2>
        <CatFactsList visibleCount={visibleCount} />
        <div>
          <button className='showmore' onClick={handleShowMore}>Show More</button>
        </div>
      </div>
      <ScrollToTopButton />
    </div>
  );
}

const mapStateToProps = state => {
  return {
    facts: state.cat_fact.facts,
    loading: state.cat_fact.loading,
    error: state.cat_fact.error,
  };
};

export default connect(mapStateToProps, { fetchCatFactsList })(HomePage);
