import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { fetchPokemon } from '../store/actions/pokemon';

function PokemonPage({ pokemon }) {

    useEffect(() => {
        fetchPokemon();
      }, []);

  return (
    <div>
        <div className="pokemon_img" src={pokemon.url}></div>
        <div>{pokemon.name}</div>
    </div>
  );
}

const mapStateToProps = state => {
    return {
      list: state.pokemon.list,
      loading: state.pokemon.loading,
      error: state.pokemon.error,
    };
  };


export default connect(mapStateToProps, { fetchPokemon })(PokemonPage);
