import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { fetchCatFactsList } from '../store/actions/cat_fact';
import '../styles/homepage.css';
import CatLoad from './cat_load';
import CatCard from './cat_card';

function HomePage({ facts, loading, error, fetchCatFactsList }) {
  useEffect(() => {
    if (!facts || !facts.list || facts.list.length === 0) {
      fetchCatFactsList();
    }
  }, [facts, fetchCatFactsList]);

  if (loading) {
    return <div><CatLoad></CatLoad></div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  const buttonsContainerRef = useRef(null);

  const scrollToButton = (buttonIndex) => {
    const buttonOffset = buttonIndex * 50;
    buttonsContainerRef.current.scrollLeft = buttonOffset;
  };


  return (
    <div className="homepage">
      <h2>Wanna know some cats facts?</h2>
      <div className='pages'>pages:</div>
      <div className='page_indicator'>
        {Array.from({ length: facts.last_page }, (_, index) => index + 1).map((value) => (
          <button key={value}>{value}</button>
        ))}
      </div>

      <div className="cat_fact_container">
        {facts && facts.list && facts.list.map((cat_fact, index) => {
          return (
            <CatCard cat_fact={cat_fact} key={index} index={index}></CatCard>
          )
        }
        )}
      </div>
    </div>
  );
}


const mapStateToProps = state => {
  return {
    facts: state.cat_fact.facts,
    loading: state.cat_fact.loading,
    error: state.cat_fact.error,
  };
};

export default connect(mapStateToProps, { fetchCatFactsList })(HomePage);
