import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { fetchCatFactsList } from '../store/actions/cat_fact';
import '../styles/homepage.css';
import CatLoad from './cat_load';

function HomePage({ facts, loading, error, fetchCatFactsList }) {
  useEffect(() => {
    if(!facts || facts?.list?.length === 0){
      fetchCatFactsList();
    }
  }, [facts, fetchCatFactsList]);

  if (loading) {
    return <div><CatLoad></CatLoad></div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="homepage">
      <h2>Wanna know some cats facts?</h2>
      <div className="cat_fact_container">
        {facts && facts.list && facts.list.map((cat_fact, index) => (
          <div key={index}>{cat_fact.fact}</div>
        ))}
      </div>
      <div>
        <button className='showmore'>Show More</button>
      </div>
    </div>
  );
}

const mapStateToProps = state => {
  return {
    facts: {
      list: state.facts.list, 
      current_page: state.facts.current_page,
      first_page_url: state.facts.first_page_url,
      last_page_url: state.facts.last_page_url,
    },
    loading: state.cat_fact.loading,
    error: state.cat_fact.error,
  };
};

export default connect(mapStateToProps, { fetchCatFactsList })(HomePage);
